package seventeenthmarchtwo;

public class SumOfNaturalNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=1,sum=0;
		for(int i=1;i<=10;i++)
		{
			sum+=num;
			num++;
		}
		System.out.println("sum of first 10 natural numbers is:"+sum+" ");


	}

}
